/*Table structure for table `car_info` */

DROP TABLE IF EXISTS `car_info`;

CREATE TABLE `car_info` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `mark` varchar(40) DEFAULT NULL COMMENT '品牌',
  `kind` varchar(40) DEFAULT NULL COMMENT '类型',
  `name` varchar(40) DEFAULT NULL COMMENT '名称',
  `conf_style` varchar(40) DEFAULT NULL COMMENT '配置（低配、中配、高配）',
  `seat_num` int(11) DEFAULT NULL COMMENT '座椅数量',
  `door_num` int(11) DEFAULT NULL COMMENT '车门数量',
  `outimg` varchar(255) DEFAULT NULL COMMENT '外观',
  `inimg` varchar(255) DEFAULT NULL COMMENT '内饰',
  `fuel_type` varchar(255) DEFAULT NULL COMMENT '燃油类型（燃气、燃油、电动）',
  `transmission_type` varchar(255) DEFAULT NULL COMMENT '变速箱类型（手动、自动、手自一体）',
  `seat_material` varchar(255) DEFAULT NULL COMMENT '座椅材质（皮质、人造革）',
  `havenavigation` int(11) DEFAULT '1' COMMENT '有无导航(1:有;0:没有)',
  `havetopwindow` int(11) DEFAULT '1' COMMENT '有无天窗(1:有;0:没有)',
  `price` float DEFAULT NULL COMMENT '车价',
  `comment` int(11) DEFAULT NULL COMMENT '数量',
  `state` int(11) DEFAULT '1' COMMENT '状态',
  PRIMARY KEY (`id`),
  KEY `FK_car_info` (`kind`),
  KEY `FK_car_info1` (`mark`),
  KEY `FK_car_info2` (`comment`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;

